template.value:
$this.dtOptions = $this.DTOptionsBuilder.newOptions()
            .withOption('ajax', {
                url        :'order/datatables',//Link request
                type       :'GET', headers:{
                    Authorization:'Bearer ' + localStorage.getItem('satellizer_token')
                },
                data       :function(data){
                    if(angular.isDate($this.date.start)){
                        data.date_start = $this.$filter('date')($this.date.start, "yyyy-MM-dd 00:00:00");
                    }
                }, complete:function(jqXHR, textStatus){//call is request success
                    $this.loadProducts();
                }
            })
            // or here
            .withBootstrap()
            .withLanguage(language)//set language
            .withDataProp('data')
            .withOption('processing', true)
            .withOption('serverSide', true)
            .withOption('order', [0, 'desc'])
            .withOption('fnRowCallback', rowCallback)
            .withPaginationType('full_numbers');
        $this.dtColumns = [
            $this.DTColumnBuilder.newColumn('<TenTrongData>').withTitle('<TenHienThi>'),
            $this.DTColumnBuilder.newColumn('total').withTitle('Tổng tiền').renderWith(function(data, type){
                return '<p class="text-center">' + $this.$filter('number')(data, '0') + '</p>';
            }),
            $this.DTColumnBuilder.newColumn('action').withTitle('Tùy chọn').withOption('searchable', false).withOption('width', '50%')
        ];

/**
     * Get data from
     * @param id
     * @param url
     * @param objData
     * @example this.loadOneData('customer/' + $this.$stateParams.id);
     */
    loadOneData(url){
        let $this = this;
        $this.API.all(url).customGET()
            .then((response) =>{
                $this.data = response.data.customer;
            });
    }

/**
     * Update data
     *
     * @param url
     * @param data
     * @example this.putData('user/' + this.$stateParams.id,this.user);
     */
    putData(url, data){
        let $this = this;
        $this.API.all(url).customPUT(data).then(function(response){
            swal('Thành công!', 'Cập nhật thành công!', 'success')
        }, function(response){
            var error = '';
            angular.forEach(response.data, function(value, key){
                error += key + ': ' + value + '\n';
            });
            swal('Lỗi', error, 'error')
        })
    }
