public function anyData( Request $request ){
			$myDatabase = \App\Customer::select( [
				'id',
				'fullname',
				'sex',
				'phone',
				'created_at',
				'state'
			] );
			//if($request->get('user_id') > 0){
			//	$myDatabase->where('user_id',$request->get('user_id'));
			//}
			//
			//if($request->get('category_id') > 0){
			//	$myDatabase->where('post_ticket_category_id',$request->get('category_id'));
			//}
			$myDatabase->get();

			return Datatables::of( $myDatabase )->addColumn( 'action',function ( $row ){
				return '<a href="javascript:;" ui-sref="app.customerview({id:' . $row->id . '})"  class="btn btn-xs btn-primary" ><i class="fa fa-eye" ></i > Chi tiết</a> ' //
				       . '<a href="javascript:;" ui-sref="app.customeredit({id:' . $row->id . '})"  class="btn btn-xs btn-primary" ><i class="glyphicon glyphicon-edit" ></i > Sửa</a> ' //
				       . ' <a href="javascript:;" ng-click="vm.delete(' . $row->id . ')" class="btn btn-xs btn-danger" ><i class="glyphicon glyphicon-remove" ></i > Xóa</a>';
				//.'<a href="javascript:;" ng-click="vm.editLoad(' . $row->id . ')" class="btn btn-xs btn-primary" ><i class="glyphicon glyphicon-edit" ></i > Sửa</a>' . 
			} )->editColumn( 'state',function ( \App\Customer $row ){
				if( $row->state == 'enable' ){
					return '<i class="fa fa-circle-o text-green"></i>';
				}else{
					return '<i class="fa fa-circle-o text-red"></i>';
				}
			} )->make( TRUE );
		}
